package com.testowyprojekt.db.testowyprojekt_db.service.base;

import java.util.ArrayList;
import java.util.List;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.transaction.annotation.Transactional;
import org.springframework.stereotype.Service;


import com.testowyprojekt.db.testowyprojekt_db.entity.Debtor;

import com.testowyprojekt.db.testowyprojekt_db.repositories.DebtorRepository;

@Service
@Transactional
public class DebtorBaseService {

	
	@Autowired
	DebtorRepository debtorRepository;


    // CRUD METHODS
    
    // CRUD - CREATE
    
	public Debtor insert(Debtor debtor) {
		return debtorRepository.save(debtor);
	}
	
	// CRUD - REMOVE
    
	public void delete(Long id) {
		debtorRepository.delete(id);
	}

	// CRUD - GET ONE
    	
	public Debtor getOne(Long id) {
		return debtorRepository.findOne(id);
	}

    	
    // CRUD - GET LIST
    	
	public List<Debtor> getAll() {
		List<Debtor> list = new ArrayList<>();
		debtorRepository.findAll().forEach(list::add);
		return list;
	}
	

}
